$(document).ready(()=>{$("#formProfile").on("submit",a=>{a.preventDefault();const b=$("#formProfile"),c=b.find("button[type=submit]");b.isValid()&&(c.attr("disabled",!0),$.ajax({type:"POST",data:b.serialize(),url:"/user/api/update/",success:function(){c.attr("disabled",!1),showSuccessToast()},error:function(a){c.attr("disabled",!1),showErrorToast(a.responseJSON.message)}}))})});